mapper.select:
select @RNUM := @RNUM + 1 num, parent_category parentCategory, sub_category subCategory, cnt from CategoryView, (SELECT @RNUM := #{param4.startCount}) R where parent_category like CONCAT('%', #{param2}, '%')
and sub_category like CONCAT('%', #{param3}, '%')
limit #{param4.startCount}, #{param4.endCount};
